char screen[HEIGHT][WIDTH];

void see(void) {
    redDetected = false;
    greenDetected = false;
    char i, j, r, g, b;
    medianFilter();
    binaryFilter();
    cleanBinaryFilter();
    printf("Red channel\n");
    for (j = 1; j < HEIGHT - 1; j++) {
        for (i = 1; i < WIDTH - 1; i++) {
            getColors(i, j, &r, &g);
            if (r > 0) {
                printf("X");
            } else {
                printf("");
            }
        }
        printf("\n");
    }
    detectChannelRed();
    for (j = 1; j < HEIGHT - 1; j++) {
        for (i = 1; i < WIDTH - 1; i++) {
            screen[j][i] = '.';
        }
    }
    if (redDetected) {
        for (i = redObject.x; i < redObject.x + redObject.w; i++) {
            screen[redObject.y][i] = 'X';
            screen[redObject.y + redObject.h][i] = 'X';
        }
        for (j = redObject.y; j < redObject.y + redObject.h; j++) {
            screen[j][redObject.x] = 'X';
            screen[j][redObject.x + redObject.w] = 'X';
        }
    }
    printf("Red object detected\n");
    for (j = 1; j < HEIGHT - 1; j++) {
        for (i = 1; i < WIDTH - 1; i++) {
            printf("%c", screen[j][i]);
        }
        printf("\n");
    }
    printf("\n");
}